import { useState } from 'react'


const Statistics = (props) => {
  return (
    <div>
    <p> good {props.good}</p>
    <p>neutral {props.neutral}</p>
    <p>bad {counter3}</p>
    <p>all {counter1 + counter2 + counter3}</p>
    <p>average {(counter1 + (counter2 * 0) + (counter3 * (-1)))/(counter1 + counter2 + counter3)}</p>
    <p>positive {(counter1 / (counter1 + counter2 + counter3)) * 100 + " %"}</p>
    </div>
  )
}

const App = () => {
  const [ counter1, setCounter1 ] = useState(0)
  const [ counter2, setCounter2 ] = useState(0)
  const [ counter3, setCounter3 ] = useState(0)

  const increaseByOne1 = () => setCounter1(counter1 + 1)
  const increaseByOne2 = () => setCounter2(counter2 + 1)
  const increaseByOne3 = () => setCounter3(counter3 + 1)

  return (
    <div>
      <h1>give feedback</h1>
      <button onClick={increaseByOne1}>
        good
      </button>
      <button onClick={increaseByOne2}>
        neutral
      </button>
      <button onClick={increaseByOne3}>
        bad
      </button>
      <h1>statistics</h1>

      <Statistics good={counter1}/>
      <Statistics neutral={counter2}/>
      <div>bad {counter3}</div>
      <div>all {counter1 + counter2 + counter3}</div>
      <div>average {(counter1 + (counter2 * 0) + (counter3 * (-1)))/(counter1 + counter2 + counter3)}</div>
      <div>positive {(counter1 / (counter1 + counter2 + counter3)) * 100 + " %"}</div>




    </div>
  )
}

export default App
