import { useState } from 'react'

const Button = (props) => {
  return (
    <button onClick={props.handleClick}>
      {props.text}
    </button>
  )
}

const StatisticLine = (props) => {
  if (props.good1 + props.neutral1 + props.bad1 === 0) {
    return (
      <p>No feedback given</p>
    )
  }
  return (
    <div>
    <p>good {props.good1} 
    <br/>neutral {props.neutral1} 
    <br/>bad {props.bad1}
    <br/>all {props.good1 + props.neutral1 + props.bad1}
    <br/>average {(props.good1 - props.bad1) / (props.good1 + props.neutral1 + props.bad1)}
    <br/>positive {props.good1 / (props.good1 + props.neutral1 + props.bad1) * 100} %</p>
    </div>
  )
}

const Statistics = (props) => {
  <div>
    <StatisticLine text="good" good1={props.good}/>
    <StatisticLine text="neutral" neutral1={props.neutral}/>
    <StatisticLine text="bad" bad1={props.bad}/>

  </div>
}

const App = () => {
  const [ counter1, setCounter1 ] = useState(0)
  const [ counter2, setCounter2 ] = useState(0)
  const [ counter3, setCounter3 ] = useState(0)

  const increaseByOne1 = () => setCounter1(counter1 + 1)
  const increaseByOne2 = () => setCounter2(counter2 + 1)
  const increaseByOne3 = () => setCounter3(counter3 + 1)

  return (
    <div>
      <h1>give feedback</h1>
      <Button handleClick={increaseByOne1}
        text="good"
      />
      <Button handleClick={increaseByOne2}
        text="neutral"
      />
      <Button handleClick={increaseByOne3}
        text="bad"
      />
      <h1>statistics</h1>

      <Statistics good={counter1} neutral={counter2} bad={counter3}/>

    </div>
  )
}

export default App
