const Header = (props) => {
    console.log(props)
    return (
      <div>
        <h1>
          {props.name}
        </h1>
      </div>
    )
  }

  const Content = (props) => {
    console.log(props)
    return (
      <div>
        <p>
          <Part pt={props.parts}/>
        </p>
      </div>
    )
  }

  const Part = (props) => {
    console.log(props)
    return (
      <div>
        <ul>
          {props.parts.map(part => <li>{part.props.name}</li>)}
        </ul>
      </div>
    )
  }

  const Total = (props) => {
    return (
      <div>
        <p>
        {"Total of exercises " + props.count}
        </p>
      </div>
    )
  }

  const Course = (props) => {

    const total = props.course.parts.reduce( (s, p) => {
      console.log('what is happening', s, p.exercises)
      return s + p.exercises
    }, 0);

    return(
        <div>
            <Header name={props.course.name}/>
            <Content parts={props.course.parts}/>
            <Total count={total}/>       
        </div>
    )

}

export default Course
