const Header = (props) => {
    console.log(props)
    return (
      <div>
        <h1>
          {props.name}
        </h1>
      </div>
    )
  }

  const Content = (props) => {
    console.log(props)
    return (
      <div>
        <ul>
        {props.course.parts.map(note => 
          <p key={note.id}>
            {note.name + " "}
            {note.exercises}
          </p>       
        )}
      </ul>
      </div>
    )
  }

  const Part = (props) => {
    console.log(props)
    return (
      <p>{props.pt[0].name + " " + props.pt[0].exercises}
      <br/>
      <br/>
      {props.pt[1].name + " " + props.pt[1].exercises}
      <br></br>
      <br></br>
      {props.pt[2].name + " " + props.pt[2].exercises}
      <br></br>
      </p>
    )
  }

  const Total = (props) => {
    return (
      <div>
        <p>
        {"Total of exercises " + props.count}
        </p>
      </div>
    )
  }

  const Course = (props) => {

    const total = props.course.parts.reduce( (s, p) => {
      console.log('what is happening', s, p.exercises)
      return s + p.exercises
    }, 0);

    return(
        <div>
          <ul>
        {props.course.parts.map(note => 
          <p key={note.id}>
            {note.name + " "}
            {note.exercises}
          </p>       
        )}
      </ul>
            <Header name={props.course.name}/>
            <Content parts={props.course.parts}/>
            <Total count={total}/>       
        </div>
    )

}

export default Course
